mapper.select:
SELECT DISTINCT MEMBER_CODE , MEMBER_ID , MEMBER_NICKNAME, MEMBER_ENROLLDATE , MEMBER_AGE, MEMBER_JOB, MEMBER_PHONE, MEMBER_GENDER, LOCATION_NAME, CATEGORY_NAME FROM( SELECT M.MEMBER_CODE, M.MEMBER_ID, M.MEMBER_NICKNAME, TO_CHAR(M.MEMBER_ENROLLDATE,'yyyy-mm-dd') AS MEMBER_ENROLLDATE, M.MEMBER_AGE, M.MEMBER_JOB, M.MEMBER_PHONE, M.MEMBER_GENDER, L.LOCATION_NAME, LISTAGG(C.CATEGORY_NAME,',') within group (order by MEMBER_ENROLLDATE) OVER (PARTITION BY M.MEMBER_CODE) AS CATEGORY_NAME FROM MEMBER M JOIN LOCATION L ON(M.LOCATION_CODE = L.LOCATION_CODE) LEFT JOIN INTERESTCATEGORY IC ON(M.MEMBER_CODE = IC.MEMBER_CODE) LEFT JOIN CATEGORY C ON(IC.CATEGORY_CODE = C.CATEGORY_CODE)) WHERE 1=1
AND CATEGORY_NAME = #{Option}
AND( MEMBER_ID LIKE '%' || #{All} || '%' OR LOCATION_NAME LIKE '%' || #{All} || '%' OR MEMBER_NICKNAME LIKE '%' || #{All} || '%' OR MEMBER_PHONE LIKE '%' || #{All} || '%' OR MEMBER_GENDER LIKE '%' || #{All} || '%' )
AND MEMBER_ENROLLDATE BETWEEN #{Date1} AND #{Date2}

mapper.update:
UPDATE NOTICE
SET NOTICE_COUNT = (SELECT NOTICE_COUNT FROM NOTICE WHERE NOTICE_CODE = #{noticeCode})+1
WHERE NOTICE_CODE = #{noticeCode}

mapper.select:
SELECT M.MEMBER_ID,M.MEMBER_PHONE, SG.STUDYGROUP_CODE, SG.STUDYGROUP_NAME, SG.STUDYGROUP_OPENSTATUS, SG.STUDYGROUP_INTRO, TO_CHAR(STUDYGROUP_STDATE, 'yyyy-mm-dd') AS STUDYGROUP_STDATE, SG.STUDYGROUP_GOALTIME, SG.STUDYGROUP_MAXNUM, SG.MEMBER_CODE, C.CATEGORY_NAME, L.LOCATION_NAME, NVL(JG.JOINGROUP_MEMBERCOUNT, 0) AS JOINGROUP_MEMBERCOUNT FROM STUDYGROUP SG JOIN LOCATION L USING(LOCATION_CODE) JOIN CATEGORY C USING(CATEGORY_CODE) JOIN MEMBER M ON(SG.MEMBER_CODE = M.MEMBER_CODE) LEFT JOIN (SELECT COUNT(MEMBER_CODE) AS JOINGROUP_MEMBERCOUNT, STUDYGROUP_CODE FROM JOINGROUP GROUP BY STUDYGROUP_CODE) JG ON(SG.STUDYGROUP_CODE = JG.STUDYGROUP_CODE) WHERE 1=1
AND (SG.STUDYGROUP_NAME LIKE '%' || #{keyword} || '%' OR C.CATEGORY_NAME LIKE '%' || #{keyword} || '%' OR L.LOCATION_NAME LIKE '%' || #{keyword} || '%' OR SG.STUDYGROUP_CODE LIKE '%' || #{keyword} || '%')
AND C.CATEGORY_NAME = #{option}
AND SG.STUDYGROUP_STDATE BETWEEN #{createDate1} AND #{createDate2}

SELECT PR_CODE,PR_COMPANY,PR_TITLE,PR_LINK,PR_ORDER,DECODE(PR_TYPE, 0, 'CPP광고', 1, 'CPC광고') AS PR_TYPE, PR_CONTRACTMONEY,CATEGORY_CODE, DECODE(PR_STATUS, 0, '게시', 1, '종료', 2, '삭제') AS PR_STATUS, TO_CHAR(PR_STARTDATE,'yyyymmdd') AS PR_STARTDATE, TO_CHAR(PR_ENDDATE,'yyyymmdd')AS PR_ENDDATE, FILES_CODE,FILES_BOARDTYPE,FILES_ORIGINNAME,FILES_NAME, FILES_ENROLLDATE,FILES_STATUS,FILES_DELETEDATE, CATEGORY_NAME FROM PR JOIN FILES USING(PR_CODE) JOIN CATEGORY USING(CATEGORY_CODE) WHERE 1=1
AND (PR_COMPANY LIKE '%' || #{keyword} || '%' OR PR_TITLE LIKE '%' || #{keyword} || '%' OR PR_LINK LIKE '%' || #{keyword} || '%')
AND CATEGORY_NAME = #{option}
AND PR_STARTDATE BETWEEN #{createDate1} AND #{createDate2}
AND PR_ENDDATE BETWEEN #{createDate1} AND #{createDate2}

SELECT COUNT(PRCOUNT_CODE)
FROM PRCOUNT
WHERE PR_CODE = #{pr_Code}
AND MEMBER_CODE = #{member_Code}

SELECT MEMBER_CODE
FROM MEMBER
WHERE PHONE = #{rphone}
