mapper.select:
SELECT M.MEMBER_CODE, M.MEMBER_ID, M.MEMBER_NICKNAME, M.MEMBER_ENROLLDATE, M.MEMBER_AGE, M.MEMBER_JOB, M.MEMBER_PHONE, M.MEMBER_GENDER, L.LOCATION_NAME, C.CATEGORY_NAME FROM MEMBER M JOIN LOCATION L ON(M.LOCATION_CODE = L.LOCATION_CODE) LEFT JOIN INTERESTCATEGORY IC ON(M.MEMBER_CODE = IC.MEMBER_CODE) LEFT JOIN CATEGORY C ON(IC.CATEGORY_CODE = C.CATEGORY_CODE) WHERE 1=1
AND( M.MEMBER_ID LIKE '%' || #{All} || '%' OR L.LOCATION_NAME LIKE '%' || #{All} || '%' OR M.MEMBER_NICKNAME LIKE '%' || #{All} || '%' OR M.MEMBER_PHONE LIKE '%' || #{All} || '%' OR M.MEMBER_GENDER LIKE '%' || #{All} || '%' )
AND M.MEMBER_ENROLLDATE BETWEEN #{Date1} AND #{Date2}
